import React, {useState,useContact} from 'react';
import ContactContext from '../../context/contact/contactContext';


const ContactForm =() => {
    const contactContext = useContext(ContactContext);
    const [contact,setContact] = useState({
        name: '',
        email:'',
        repos:'',
        location:'',
        language:''
    });
    const {name, email, repos, location,language} = contact;

    const onChange = e => setContact({...contact,[e.target.name]:e.target.value});

    const onSubmit = e => {
        e.preventDefault();
        contactContext.addContact(contact);
        setContact({
        name: '',
        email:'',
        repos:'',
        location:'',
        language:''
        })
    }
    return (
        <form onSubmit={onsubmit}>
            <h2 className="text-primary">Add Contact</h2>
            <input
                type='text'
                placeholder='name'
                name='name'
                value={name}
                onChange={onChange}
            />
             <input
                type='text'
                placeholder='Email'
                name='email'
                value={email}
                onChange={onChange}
            />
              <input
                type='text'
                placeholder='Number Of Repos'
                name='repos'
                value={repos}
                onChange={onChange}
            />
              <input
                type='text'
                placeholder='Location'
                name='location'
                value={location}
                onChange={onChange}
            />

            <input
                type='text'
                placeholder='Language'
                name='language'
                value={language}
                onChange={onChange}
            />
        <div>
            <input type="submit" value="Add Contact" className="btn btn-primary btn-block"/>
        </div>
        </form>
    )
}

export default ContactForm
