import {
    GET_USERS,
    USERS_ERROR,
    USER_LOADED,
    REGISTER_SUCCESS,
    LOGIN_SUCCESS

  } from '../types';

  export default (state, action) =>{
      switch(action.type){
        case GET_USERS:
            case USER_LOADED:
                return {
                  ...state,
                  isAuthenticated: true,
                  users: action.payload,
                  loading: false
                };
                case REGISTER_SUCCESS:
                  case LOGIN_SUCCESS:
                      localStorage.setItem('token',action.payload.token);
                      return{
                          ...state,
                          ...action.payload,
                          isAutheticated:true,
                          loading:false
                      };
        case USERS_ERROR:
            return {
                ...state,
                error:action.payload
            };
            default:
                return state;
      }
  }
