import React, {useState} from 'react'

const ContactForm =() => {
    const [contact,setContact] = useState({
        name: '',
        email:'',
        repos:'',
        location:'',
        language:''
    });
    const {name, email, repos, location,language} = contact;

    const onChange = e => setContact({...contact,[e.target.name]:e.target.value});
    return (
        <form>
            <h2 className="text-primary">Add Contact</h2>
            <input
                type='text'
                placeholder='name'
                name='name'
                value={name}
                onChange={onChange}
            />
             <input
                type='text'
                placeholder='Email'
                name='email'
                value={email}
                onChange={onChange}
            />
              <input
                type='text'
                placeholder='Number Of Repos'
                name='repos'
                value={repos}
                onChange={onChange}
            />
              <input
                type='text'
                placeholder='Location'
                name='location'
                value={Location}
                onChange={onChange}
            />

            <input
                type='text'
                placeholder='Language'
                name='language'
                value={language}
                onChange={onChange}
            />
        <div>
            <input type="submit" value="Add Contact" className="btn btn-primary btn-block"/>
        </div>
        </form>
    )
}

export default ContactForm
