import React, { Component } from 'react';
import './Search.css';

export class Search extends Component {
  state = {
    text: ''
  };

  onSubmit = e => {
    e.preventDefault();
    // Sends props back up to App.js with Alert Message when triggered
    if (this.state.text === '') {
      this.props.setAlert('Please enter a search term');
    } else {
      // Sends props back up to App.js with "this.state.text"
      this.props.searchUsers(this.state.text);
      this.setState({ text: '' });
    }
  };

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    return (
      <div>
        <form onSubmit={this.onSubmit} className='form'>
          <input
            type='text'
            name='text'
            placeholder='Search By Location'
            value={this.state.text}
            onChange={this.onChange}
          />
           <input
            type='text'
            name='repositories'
            placeholder='Min # of Repos'
            value={this.state.repositories}
            onChange={this.handleInputChange}
          />
           <input
            type='text'
            name='language'
            placeholder='Programming Language'
            value={this.state.language}
            onChange={this.handleInputChange}
          />
          <input type='submit' value='Search' className='ButtonStyle' />
        </form>


      </div>

    );
  }
}

export default Search;
