import React, { useReducer } from 'react';
import uuid from 'uuid';
import ContactContext from './contactContext';
import contactReducer from './contactReducer';
import {
  GET_CONTACTS,
  ADD_CONTACT,
  DELETE_CONTACT,
  SET_CURRENT,
  CLEAR_CURRENT,
  UPDATE_CONTACT,
  FILTER_CONTACTS,
  CLEAR_CONTACTS,
  CLEAR_FILTER,
  CONTACT_ERROR
} from '../types';

const ContactState = props => {
  const initialState = {
    contacts: [
      {
        id: 1,
        name: 'Jill Johnson',
        email: 'jill@gmail.com',
        phone: '111-111-1111',
        type: 'personal',
        repos: '333',
        location:'asdas',
        language: 'sssss'
      },
      {
        id: 2,
        name: 'Sara wwwww',
        email: 'aaaa@gmail.com',
        phone: '222-222-2222',
        type: 'personal',
        repos: '333',
        location:'asdas',
        language: 'sssss'
      }
    ],

    current:null

  };
   const [state, dispatch] = useReducer(contactReducer, initialState);

   //Add Contact
   const addContact = contact =>{
     contact.id=uuid.v4();
     dispatch({type: ADD_CONTACT, payload:contact});
   };


   //Delete contact
   const deleteContact = contact =>{
    dispatch({type: DELETE_CONTACT, payload:contact});
  };
   //Set Current Contact
   const setCurrent = contact =>{
    dispatch({type: DELETE_CURRENT, payload:contact});
  };
   //Clear Current Contact
   const clearCurrent = () =>{
    dispatch({type: DELETE_CURRENT});
  };
   //Update Contact
   //Filter Contacts
   //Clear Filter

  return (
    <ContactContext.Provider
    value={
      {
        contacts: state.contacts,
        current: state.current,
        addContact,
        deleteContact,
        setCurrent,
        clearCurrent

      }}>
      {props.children}
    </ContactContext.Provider>
  )
};

export default ContactState;
