import React from 'react';

const ContactItem = ({contact}) => {
    const {id, name, email, repos, location,language} = contact;
  return (
    <div className='card bg-light'>
        <h3 className="text-primary text-left">
           {name}{''}
           <span
           className={
             'badge'+repos
           }
           >
             {repos.charAt(0).toUpperCase()}
           </span>
        </h3>
        <h3 className="list">
           {email && (<li>
             <i className="fas fa-envelope-open"></i>{email}
           </li>

           )}
        </h3>
        <h3 className="text-primary text-left">
            Repos:{repos}{''}
        </h3>
        <h3 className="text-primary text-left">
            Location: {location}{''}
        </h3>
        <h3 className="text-primary text-left">
            Language:{language}{''}
        </h3>
    </div>
  );
};

export default ContactItem;
